INSERT IGNORE INTO settings(setting, name, value, hint) VALUES('timeoutpath', 'timeoutpath', '', 'This is used to limit the amount of time unrar/7zip/avconv/mediainfo can run. You can the time limit in the process additional section. You can leave this empty to disable this. Use forward slashes in windows c:/path/to/timeout.exe');
INSERT IGNORE INTO settings(setting, name, value, hint) VALUES('timeoutseconds', 'timeoutseconds', '0', 'How much time to wait for unrar/7zip/avconv/mediainfo before killing it, set to 0 to disable. We do not run ffmpeg with timeout as it freezes, avconv however does not freeze. 60 is a good value. Requires the GNU Timeout path to be set.');